css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.waze.com/editor/"),url("https://www.waze.com/editor/#")  {
.waze-header {
  height: 24px !important;
}
.waze-header-logo {
  background-size: 60px !important;
  margin: 4px 0px auto auto !important;
  background-position: top center !important;
}
.waze-header-menu {
  line-height: 25px !important;
  font-size: 11px !important;
}
#map-search {
  line-height: 60px !important;
}
#toolbar.toolbar-button {
  width: 35px !important;
  height: 40px !important;
}
#sidebar {
  width: 260px !important;
}
.show-sidebar .row-fluid .fluid-fixed {
  margin-left:260px
}
#user-info h2 {
  display:none !important;
}
#sidebar .message {
  font-size: 12px !important;
  line-height: 14px  !important;
  margin: 5px  !important;
}
#sidebar .tab-content {
  padding: 3px 15px 10px 9px !important;
}
#sidebar #links {
  width: 320px !important;
  font-size: 10px !important;
  background-color: #e9e9e9 !important;
  border-right: #dfdfdf thin solid !important;
}
#chat {
    width: 275px !important;
}
#chat-overlay {
  right: 0px !important;
}
#chat .header {
  line-height: 20px !important;
}
.messages .message-list  {
	max-height: 350px !important;
  font-size: 12.5px !important;
  width: 275px !important;
}
#chat .messages .new-message input {
  width: 75% !important;
}
#chat .users {
	display: none !important;
}
.olControlAttribution {
  margin-left: 45px !important;
}
#WMETB_NavBar.ui-draggable {
  left: 5px !important;
  width: 234px !important;
  height: 80px !important;
  line-height: 22px !important;
  color: #000 !important;
  background-color: #dfdfdf !important;
  border: #59899E 2px solid !important;
}
}
