css:
@-moz-document url-prefix(http://tgmonline.gamesvillage.it) {
  body {
    width: 95% !important;
    margin-top: 20px !important;
  }
  
  body #stickies a, body #threads a, body #forums a {
    color: #444 !important;
  }

  body #stickies a.threadtitle_unread, body #threads a.threadtitle_unread {
    color: #000 !important
  }

  /* fix per le immagini che sforano la larghezza */
  .content img {
    max-width: 100%;
  }

  .navpopupbody.popupbody {
    overflow-x: hidden;
  }

  .popupbody li > a, .popupbody li > label {
    padding: 12px !important
  }
  
  .box-cont-all, #ad_global_header2, #takeover_gv {
    display: none;
  }
}
