css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix("https://www.tumblr.com"), url-prefix("https://www.tumblr.com/")

{

.post_full .post_permalink:before
{
content: none !important;
}
  
.identity-refresh .post_full .post_permalink
{
border-style: none !important;
}

.post_full:hover  .post_permalink 
{
width: 16px !important;
height: 16px !important;
top: 1px !important;
right: 1px !important;
overflow: visible !important;
opacity: 1 !important;
background: url("http://img.photobucket.com/albums/v420/KaKaVegeGurl/Page2_zps15c473c8.png") no-repeat scroll 0% 0% transparent !important;
}

.post_full:hover
{
border-radius: 6px 22px 3px 6px !important;
}

#posts.posts_v2 .new_post_buttons:hover 
{
border-radius: 6px 6px 6px 6px !important;
}

#posts.posts .new_post_buttons 
{
border-radius: 6px 6px 6px 6px !important;
}
}
