@-moz-document domain("yorkbbs.ca") {
  
/*移除头部，脚部广告*/
#five_pic_head, #five_pic_foot, .five_pic_head { display: none !important; }

/*移除左下角广告*/
#forum-showforum-left-bottomads { display: none !important; }

/*移除主题贴左下角广告*/
#showtopic_ads { display: none !important; }

/*移除右侧广告*/
#NLeftPic a[target="_blank"] { display: none !important; }

/*移除中央顶部的窄条置顶和文字广告*/
#forumrighttopads { display: none !important; }
.tabtextgg { display: none !important; }

/*移除签名广告*/
.t_signature { display: none !important; }

/*移除花钱加红加粗的帖子主题*/
span[style="font-weight:bold;color:#FF0000;"] {font-weight:normal !important; color:#454545 !important;}

/*---------------------------------------- 以下为BBS首页广告清理 ----------------------------------------*/
.w_rgg, #parentmph { display: none !important; }
div[style=" width:409px; height:70px;"] { display: none !important; }

}
