css:
@-moz-document url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {
/* CSS by no-html.tumblr.com */
/* Version 1.2 */
/* Licensed under the Creative Commons Attribution-NonCommercial-ShareAlike 4.0 International Public License */

/* https://userstyles.org/styles/106900/tumblr-modern-sharp */

.identity-refresh .l-container.l-container--two-column-dashboard .left_column {background-color: transparent;}
        
#right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column {background-color: transparent; margin-top: 0px;}

#dashboard_controls {margin-top: 16px;}

#dashboard_controls .control {color: rgba(255,255,255,0.5);	text-decoration: none; text-shadow: transparent;}

.l-header-container--refresh {background-color: rgba(54,70,93,1); opacity: 0.9;}

.compose-button {border-color: transparent; background: rgba(255,255,255,0.5); -moz-border-radius: 0px; border-radius: 0px;}

.xtimestamp {margin-left: 20px;}

#new_post_in_tracked_tags_bubble.search_query_btn.pill {margin-top: -26px;}

.identity-refresh .post.post_full{border-radius: 0px;}

.post_brick {border-radius: 0px;}

.indash_header_wrapper .header_image img {opacity: 1;}

.identity-refresh .tumblelog_popover .recent_posts {border-radius: 0px;}

.identity-refresh .post_full .post_permalink {border-radius: 0px 0px 0px 0px;}

.identity-refresh .post_avatar .post_avatar_link {border-radius: 0px;}

.identity-refresh .controls_section.user_list .avatar_img {border-radius: 0px; border-top-left-radius: 0px; border-top-right-radius: 0px; border-bottom-right-radius: 0px; border-bottom-left-radius: 0px; opacity: 1;}

.chrome {border-radius: 0px;}

.identity-refresh .popover_gradient > .popover_inner:last-child, .identity-refresh .popover_gradient.popover_blogs > .popover_inner:last-child {border-radius: 0px;}

.identity-refresh .right_column:after {background: transparent;}
      
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before {background-image: none;	border-right: 0px;}

.identity-refresh .radar .radar_footer .radar_avatar:before, .radar .radar_footer .radar_avatar:before {background-image: none; background: transparent;}

.radar .radar_post {border-radius: 0px 0px 0px 0px;}

.radar .radar_avatar img {border-radius: 0px;}

.radar .radar_controls {border-radius: 0px 0px 0px 0px;}
      
.l-header .selection_nipple {border-bottom: 12px solid #6C9526;}
        
.identity-refresh .right_column .follow {color: rgba(255,255,255,0.5);}

.elevator {font-size: 64px; _margin-bottom: 60px; margin-bottom: 10px; _margin-right: 19px; color: rgba(255,255,255,0.3);}

.elevator-wrapper {right: 30px; top: 95px;}

.ui_search.tab.active .search_query, .ui_search.tab.active .search_form_field:before {color: rgba(255,255,255,0.5); border-color: rgba(255,255,255,0.5);}
}
