css:
@-moz-document url-prefix("http://localhost"), url-prefix("http://127.0.0.1") {
html,body,.wrapper{
  background-color: #F5F8FA;
}

.module{
    border-radius:3px;
}

.mini-profile-photo,.avatar,.post-photo{
    border-radius:5px;
}

.posts-count,.following-count,.followers-count{
	font-family: Georgia !important;
    font-size: 20px !important;
    color: #1F94B8 !important;
}
    
.mini-profile-name{
    color:#292F33 !important;
}
  
.profile-data li{
    border:none;
}

.post-text, h1, h2, h3, h4, h5, h6, p, .open-hashtag-modal, .post-info-time,span{
    font-family: "Roboto" !important;
  	font-family: "Roboto","Noto Sans S Chinese" !important;
}
  
.open-hashtag-modal,.post-info-name, .open-profile-modal,.post-expand{
  	font-family: "Roboto","Noto Sans S Chinese" !important;
    font-weight:normal;
}
    
.twister-user-tag{
    font-style:normal;
    font-weight:normal;
}

.open-hashtag-modal,.twister-user-tag,.followed-by,.refresh-users,.view-all-users,.open-profile-modal,.post-expand,.post-reply,.post-propagate{
    color: #1F94B8 !important;
}
    
a:hover{
    text-decoration:none;
}

.postboard h2{
    color:#66757F;
    background:none;
  	font-variant:normal;
}

.open-following-page:hover,.open-profile-modal:hover,.open-followers:hover{
    color: #1F94B8 !important;
}

.userMenu{
    background: #1F94B8;
}

button{
    border-radius:5px;
    color: #F7F9FB;
}

.post-info-name{
    font-weight:500;
  	font-size:17px;
}

.post-text{
    font-size:16px;
}
}
