css:
@-moz-document url-prefix("http://episodecalendar.com/calendar") {
body {
    overflow:hidden;
}

#page_container {background: #ccc !important;}

.calendar_day, .calendar_day_other_month {background:#ddd !important;}
th div {background-color:#ccc !important; color: #111 !important;}
.calendar_today {background:#999 !important;}

.calendar_date {background-color:#d6d6d6 !important;}

.margin_bottom_small, .adblocker-warning {display:none !important;} 

#calendar {padding-top:20px !important;}

div[class="center"]{display:none !important;}
a[href="http://episodecalendar.com"]{display:none !important;}
}
