css:
@-moz-document domain("torrentleech.org") {
#torrents table.display td.name {
    padding: 0 !important;
    color: #666 !important;
    font-size: 0 !important;
  }

  #torrents table.display td.name a {
    font-size: 12px;
  }

  #torrents table tr td {
    font-size: 11px !important;
    height: 16px !important;
    overflow: hidden;
    white-space: nowrap;
  }

  #torrents img {
    width: 16px;
  }

  #torrents table.display td.category {
    padding-right: 1em;
  }

  #wrapper #main #middle {
    display: none;
  }

  td .newicon {
    color: red ! important;
    display: inline;
    letter-spacing: 0.5px;
    font-weight: bold;
    font-size: 8px;
    margin-left: 1em;
  }

  #supportTorrentLeech { 
    display:none
  }

  #searchForm legend {
    display: none;
  }

  .pagination {
    float: left;
    margin-left: 40px;
  }

  #main .torrentTools {
    margin: 5px 0 !important;
  }

  #torrents + .torrentTools + .clearfloat {
    clear: none !important;
  }

  #torrents tr td:nth-child(6) > br {
    display: none;
  }

  #torrents th:nth-child(9), #torrents tr td:nth-child(9) {
    display: none;
  }

  form input.txt {
    background: white ! important;
    padding: 2px ! important;
    font: 12px Tahoma, Geneva, Verdana, Helvetica, Arial, sans-serif ! important;
    letter-spacing: 0.5px;
  }
}
