css:
@-moz-document url-prefix("http://music.163.com/") {
@media screen and (max-width:980px) and (min-width:867px){
.m-playbar .wrap,.m-playbar .list{width:100%!important;margin-left:-50%!important}
.m-playbar .btns{width:16%!important}
.m-playbar .play{width:53%!important}
.m-playbar .oper{width:8%!important;margin-left:10px;}
.m-playbar .ctrl{width:14%!important}
.m-pbar,.m-pbar .barbg{width:90%!important}
.m-pbar .time{right:-10%}
.m-playbar .listhdc .lytit,.m-playbar .listlyric{width:35%}
.m-playbar .listhdc .lytit{right:60%}
.m-playbar .listlyric{right:10%}
}
@media screen and (max-width:866px) and (min-width:769px){
.m-playbar .wrap,.m-playbar .list{width:100%!important;margin-left:-50%!important}
.m-playbar .btns{width:16%!important}
.m-playbar .play{width:50%!important}
.m-playbar .oper{width:8%!important;margin-left:8px;}
.m-playbar .ctrl{width:16%!important}
.m-pbar,.m-pbar .barbg{width:100%!important}
.m-pbar .time{position:relative;left:82%;top:-25px}
.m-playbar .listhdc .lytit,.m-playbar .listlyric{width:44%}
.m-playbar .listbdc{width:56%}
.m-playbar .listhdc .lytit{left:60%}
.m-playbar .listlyric{right:10%}
}
@media screen and (max-width:768px){
.m-playbar{margin-top:0;top:-100px!important;background-image:linear-gradient(#3e3e3e,#000);background-color:#3e3e3e;height:100px!important}
.m-playbar .updn .left,.m-playbar .updn .right{height:50px}
.m-playbar .bg{height:50px}
.m-playbar .oper{width:60px;position:relative;top:-70px;left:60%}
.m-playbar .ctrl{position:relative;top:-70px;left:60%}
.m-playbar .btns{width:157px;padding:6px 0 0 6px;position:relative;left:35%}
.m-playbar .play{position:relative;width:80%}
.m-playbar .list{bottom:97px}
.m-playbar .list{width:100%!important;margin-left:-50%!important}
.m-playbar .listhdc .lytit{display:none;}
.m-playbar .listlyric{display:none;}
}
}
