css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru") {
  html, body { color: #bdbdbd; background-color: #343434 !important; }
 
  .messages .msg, .infoblock, .boxlet, #user-menu, #contents, ul.tab-menu li, #nav-menu, .page-number {
      background-color: #282828 !important;
      border-radius: 4px !important; /* <<< радиус скругления углов*/ }
  #nav-menu ul { border-bottom: 1px solid #444444;}
  .code, #content-article pre, #toc { background-color: #343434 !important; }
  
  a, #hd .menu a { color: #8097A7 !important; }
  h1 a, h2 a, td a, .nav a { color: #cccccc !important; }
  #bd a.tag { color: #CABEB5 !important; }
  #bd h2 *{ color: #bdbdbd; }
  
  #bd .forum table tbody tr:hover {background-color: #513536 !important; }
  
  .group-label { background-color: #715E3E !important; }
  
  .messages .msg:target { border: 1px solid #962727; }
  
  .stars, #bd .sign a { color: #aaccaa !important; }
  
  #bd .btn-default, #bd .nav li a { color: #bdbdbd !important; background-color: #444444 !important; }
  #bd .btn-selected { color: #111111 !important; background-color: #999999 !important; }
  #bd .nav li a.current { background-color: #bababa !important; }
  #bd .btn-primary { color: #bdbdbd !important; background-color: #434E5F !important; }
  #bd .btn-danger { color: #bdbdbd !important; background-color: #793030 !important; }
  #bd .btn:hover, #bd .nav li a:hover { box-shadow: 0px 0px 1px 1px #666666; }
  
  * img, .penguin_progress span { opacity: 0.5; }
  * img:hover { opacity: 1; }
  /*photo { opacity: 0.5; }
  photo:hover { opacity: 1; }*/
}
