css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
    .DashboardProfileCard-avatarLink, .DashboardProfileCard-avatarImage, .avatar{
      border-radius:100% !important;
    }
    #global-actions li:nth-last-child(1){
     display:none;
    }
    .import-prompt{
      display:none;
    }
    .DashboardProfileCard{
      height:450px !important;
    }
  
    .home-tweet-box{
      position:absolute !important;
      margin:190px 0 0 -300px;
      width:265px;
    }
    .top-timeline-tweet-box-user-image{
      display:none;
    }
    #tweet-box-mini-home-profile{
      /*position:absolute !important;*/
      margin:0 0 0 -60px;
      width:250px;
    }
    .tweet-box-extras{
      margin-left:-65px;
    }
    .btn primary-btn, .tweet-action, .disabled, .tweet-btn, .js-tweet-btn{
      width:260px;
      margin-top: 10px;
    }
}
