css:
/* i really want this to be global */
#page.watch #player {width:100% !important;max-width:100% !important;margin-top:0 !important; margin-bottom:10px !important;}
#page.watch #player .player-height {height:auto !important;}
#page.watch #player .player-width {width:100% !important;}
#page.watch #player-api .html5-video-container {position:static !important;width:100% !important;}
#page.watch #player-api video.video-stream.html5-main-video {position:static !important;width:100% !important;height:100% !important;}
#page.watch #watch7-sidebar {margin-top:0px !important;top:0 !important;}
#page.watch #watch-header, #page.watch #watch-header.yt-card {margin-top:0 !important;}
#page.watch div.ytp-chrome-bottom{width:99% !important;}
#page.watch div.ytp-iv-video-content {width: 640px;height: 360px;}
#page.watch div#placeholder-player{margin-bottom:23% !important;}
@media screen and (min-width:960px) {#page.watch div#placeholder-player{margin-bottom:28% !important;}}
@media screen and (min-width:1110px) {#page.watch div#placeholder-player{margin-bottom:32% !important;}}
@media screen and (min-width:1300px) {#page.watch div#placeholder-player{margin-bottom:28% !important;}}
@media screen and (min-width:1480px) {#page.watch div#placeholder-player{margin-bottom:32% !important;}}
