css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("diary.ru") {

body {
    background:none;
}

body {
    background-color:#000 !important;
    color: #ddd !important;
}
  hr {
   color:#222 !important; 
  }
body {
    filter: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg'><filter id='grayscale'><feColorMatrix type='matrix' values='0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0'/></filter></svg>#grayscale"); /* Firefox 10+, Firefox on Android */
    filter: gray; /* IE6-9 */
    -webkit-filter: grayscale(100%); /* Chrome 19+, Safari 6+, Safari 6+ iOS */ 
}
.paragraph div:first-letter {
 color:#eee; 
}
a {
  color:#bbb !important;
text-decoration: underline !important;
}  
.block_design .bordered {
    background-color: #000;
    border-color: #222;
}  
.menuSection, #epigraph, .formcontainer, .pageBar, .postContent, .postLinksBackg, .postTitle, .authorName, .communityName, .order_button, .section, .list_box {
    background-color: #000  !important;
}  
.block_design .singlePost, .block_design .menuSection, .block_design .singleComment, .block_design .section, .block_design #epigraph, .block_design .pageBar, .block_design .group_link, .block_design .list_box {
    border-color: #222;
}
.htmlbox textarea, input.text, #trspisok textarea, #close_text {
    color: #999;
}

#thisDiaryOwner ul, #myDiaryLinks ul, #myCommunity ul, #globalLinks ul, #myLinks ul, #thisDiaryLinks ul {
    background-color: #000;
}
fieldset.box, div.box, form.box {
    background-color: #000;
}
table.box {
    background: none repeat scroll 0% center #111;
    border: 1px solid #444;
}
.textbox, input.text, select, input.i, textarea,#shsmile, #jsbut {
background-color:#111;
}        
}
