css:
@-moz-document domain(wikipedia.org) {



body
{
    font: 14px/1.5 Open Sans, sans-serif;

    color: #333;
    background-color: #e6e6e6;
}

#content
{
    font-size: 24px;

    margin: 0;

    border: none;
}

@media (min-width: 1441px)
{
    #content
    {
        max-width: 1000px;
        margin-right: auto;
        margin-left: auto;
    }
}

#content   p
{
    padding-bottom: 10px;
}

#firstHeading
{
    font-size: 2em;
}

#mw-page-base,
#mw-head-base,
#mw-panel,
#mw-head,
#footer,
#siteSub
{
    display: none;
}

#simpleSearch
{
    display: block;

    margin: 20px   0;
}



@media (min-width: 1441px)
{
    #toc
    {
        font-family: 'Helvetica Neue';
        font-size: 18px;

        position: fixed;
        top: 0;
        left: 0;

        display: block;
        overflow: auto;
        overflow: hidden;
        overflow-y: auto;

        width: 300px;
        height: 100%;
        margin: 0;
        padding: 20px;
    }

    #toc > ul
    {
        display: table-cell;
        overflow-y: auto !important;

        height: auto;
        margin: auto 0 !important;

        vertical-align: middle !important;
    }

    .toc ul ul
    {
        margin-left: 1em !important;
    }

    .toc a
    {
        line-height: 1.25 !important;

        display: block;

        padding: .25em 1em;
    }

    .toclevel-1 > a > .toctext
    {
        font-weight: 600;

        display: inline-block;

        padding-top: .25em;

        text-transform: uppercase;
    }

    .tocnumber
    {
        display: none;
    }

    .toccolours,
    .thumb
    {
        border: 1px solid #eee !important;
        border-radius: 2px !important;
    }

    div.toctitle
    {
        text-decoration: none !important;
    }

	#togglelink 
	{
  		display: inline;
	}

    table.infobox
    {
        font-size: 16px;
        line-height: 18px;

        position: fixed;
        top: 0;
        right: 0;

        display: block;
        overflow: auto;

        width: 400px;
        height: 100%;
        padding: 20px;

        border: 0;
        border-left: solid   #ccc   1px;
    }


    table.infobox   td.fn
    {
        background: inherit;
    }

    table.infobox   th
    {
        background: inherit;
    }

    table.infobox.vcard
    {
        position: fixed;
        top: 0;
        right: 0;

        overflow: auto;

        width: 400px;
        height: 100%;
        margin-top: 0;
        margin-right: -41px;
        padding: 20px;

        border: 0;
        border-left: solid   #ccc   1px;
    }

    table.infobox.vcard   td.fn
    {
        background: inherit;
    }

    table.infobox.vcard   th
    {
        background: inherit;
    }

    table.infobox.vevent.noprint.collapsible
    {
        display: none;
    }
}

}
