css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("golem.de") {

/**
 * User style for german language news site http://www.golem.de.
 * (http://userstyles.org/styles/10704)
 *
 * v1.4 
 *
 * Feel free to make suggestions and report bugs:
 * http://forum.userstyles.org/post.php?CategoryOverride=10508
 */

 /**
  * Full width for homepage, articles and forum
  */
 table[width="972"] {
  width: 100% !important;
 }

 /**
  * Full width for screenshot overviews
  */
 table[width="932"] {
  width: 100% !important;
 }

 /**
  * Full width for tables with subheaders
  */
 table[width="480"] {
  width: 100% !important;
 }

 /**
  * Reduce page height to visible content
  */
 td.leftedge img {
  height: 100% !important;
 }

 /**
  * Left alignment for newsletter input field 
  */
 td.headerbg form table[align="right"] {
  align: left !important;
 }

 /**
  * Fixed width for right column in forum pages
  *
  * Thanks to DreadKing and ChoGGi!
  * (http://forum.userstyles.org/comments.php?DiscussionID=2560)
  */
 table[width="972"]>tbody>tr>td[valign="top"][rowspan="3"] {
  width: 280px !important;
 }

 /**
  * Full width for forum
  */
 #body {
  width: 100% !important;
 }

 /**
  * Full width for header on forum page
  */
 #header {
  width: auto !important;
 }
 
 /**
  * Full width for header inputs on forum page
  */
 #inputs {
  width: auto !important;
 }

 /**
  * Full width for content on forum page
  */
 #content {
  width: auto !important;
 }

 /**
  * Full width for forum posts on forum page
  */
 #phorum {
  max-width: 100% !important;
 }

 /**
  * Full width for day headers in ticker.
  */
 .archdaterow {
  width: 100% !important;
 }

 /**
  * Full width for API page
  */
 body {
  width: auto !important;
 }

 /**
  * Full width for footer on API page
  */
 .footer {
  width: auto !important;
 }

 /**
  * Remove header ad
  */
 #big {
  display: none !important;
 }

 /**
  * Remove skyscraper ad
  */
 table td[rowspan="5"] {
  width: 0 !important;
 }

 /**
  * Remove skyscraper ad
  */
 table td[rowspan="5"] #sky {
  display: none !important; 
 }

 /**
  * Remove ad in the article
  */
 #contentad {
  display: none !important;
 }

 /**
  * Remove ad in the article
  */
 #contentad2 {
  display: none !important;
 }

 /**
  * Remove footer ad
  */
 div[style="margin-top: 10px; clear: both;"] {
  display: none !important
 }

 /**
  * Remove ad at the top of the menu
  * (maybe to specific)
  *
  * Uncommented due to bug in FF 3.6:
  * https://bugzilla.mozilla.org/show_bug.cgi?id=541506
  * (Not really necessary if adblock is used.)
  *
  * Thanks to michelsberg and TurboDuke!
  * (http://forum.userstyles.org/comments.php?DiscussionID=16384)
  *
  td.headerbg div, td.headerbg h5[style="text-transform: none; text-align: center; background-color: rgb(184, 23, 26);"] {
  display: none !important;
  */
 }

}
