css:
@-moz-document domain("www.opensubtitles.org") {
body {
  background-color: #000 !important;
}

div {
  color: #FFF !important;
  background-color: #111 !important;
}

table {
  color: #FFF !important;
  border-color: #FFF !important;
}

th {
  color: #000 !important; 
}

.even, .sb_star_even {
  background-color: #111 !important;
}

.odd, .sb_star_odd {
  background-color: #222 !important;
}

.featured, .sb_star_featured {
  background-color: #0C380A !important;
}

.a.p.a {
  color: #D16200 !important        
}

.footer a {
  background-color: #111;
}

#ac {
  background-color: FFF !important;
}

#lang_footer a {
  background-color: #111;
}

#lang_footer a:hover {
  color: #D16200;
}

#footerlinks a:hover {
  color: #185DA0;
}
}
