css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  /* The Jargon File */
  url-prefix("http://www.catb.org/jargon/html/"), url-prefix("http://catb.org/jargon/html/"),
  /* The Art of Unix Programming */
  url-prefix("http://www.catb.org/~esr/writings/taoup/html/"), url-prefix("http://catb.org/~esr/writings/taoup/html/"),
  /* The Art of Unix Usability */
  url-prefix("http://www.catb.org/~esr/writings/taouu/html/"), url-prefix("http://catb.org/~esr/writings/taouu/html/"),
  /* A Brief History of Hackerdom */
  url-prefix("http://www.catb.org/~esr/writings/cathedral-bazaar/hacker-history/"), url-prefix("http://catb.org/~esr/writings/cathedral-bazaar/hacker-history/"),
  /* The Cathedral and the Bazaar */
  url-prefix("http://www.catb.org/~esr/writings/cathedral-bazaar/cathedral-bazaar/"), url-prefix("http://catb.org/~esr/writings/cathedral-bazaar/cathedral-bazaar/"),
  /* The Unix Koans of Master Foo */
  url-prefix("http://www.catb.org/~esr/writings/unix-koans/"), url-prefix("http://catb.org/~esr/writings/unix-koans/"),
  /* Conventions at Light Speed */
  url-prefix("http://www.catb.org/~esr/writings/sfshows/"), url-prefix("http://catb.org/~esr/writings/sfshows/"),
  /* The Nerligig Papers */
  url-prefix("http://www.catb.org/~esr/writings/nerligig/"), url-prefix("http://catb.org/~esr/writings/nerligig/"),
  /* Unix Wars */
  url-prefix("http://www.catb.org/~esr/writings/unixwars.html"), url-prefix("http://catb.org/~esr/writings/unixwars.html"),
  /* Tengwar modes */
  url-prefix("http://www.catb.org/~esr/tengwar/"), url-prefix("http://catb.org/~esr/tengwar/")
{
  body {
    font-size: 20px;
    line-height: 1.3;
    
    max-width: 65ch;
    margin-left: auto;
    margin-right: auto;
    
    padding: 1em;
  }
  
  pre, code, tt, kbd, samp {
    font-size: 90%;
  }
  
  /* shrink text on smaller pages so that the number of
   * characters per line is still readable
   */
  @media (max-width: 600px) {
    body {
      font-size: 18px;
    }
  }
  @media (max-width: 420px) {
    body {
      font-size: 16px;
    }
  }
}
