css:
@-moz-document domain("player.me") {
.page-root.feed-bg {
    background-image:url('http://cdn.akamai.steamstatic.com/steamcommunity/public/images/items/440/4fac253144bc134a3f6bac4aa23036ade05716eb.jpg');
    background-repeat:no-repeat;
    background-size:cover;
    background-attachment:fixed
}
 
/* Hides Posted by / Suggested Users */
.feed-container .activity .source,.suggestion.card {
    display:none
}
 
/* User Plate */
.col-lg-3 {
    float:left;
    position:relative;
    width:350px
}
 
/* Feed Plate */
.col-lg-7 {
    float:right;
    position:relative;
    margin-right:120px;
    width:900px
}
 
/* Online Now Plate */
.col-lg-2 {
    float:left;
    position:relative;
    margin-top:10px;
    width:350px
}

#top-bar .messaging .unread, #top-bar .notification-container .notifications .unread {
	background: #dadada;
}

#top-bar .messaging a, #top-bar .notification-container .notifications .unread a {
	color: #000;
}
}
