css:
@-moz-document domain("youtube.com") {
#header, #feed, #guide, #google-cookie-alert, #yt-lang-alert-container, .cookie-alert, .annotation, .annotation-shape, #watch-discussion,
#watch7-subscription-container, #watch8-secondary-actions, #yt-masthead-signin, #appbar-guide-button-container, #watch-action-panels, .yt-user-photo,
.videowall-endscreen, #masthead-appbar-container
{ display: none !important; }
#watch7-sidebar, #footer-container {
    opacity: 0.15;
    transition: opacity 200ms ease-in-out;
}
#watch7-sidebar:hover, #footer-container:hover {
    opacity: 1;
}
    
}
