css:
@-moz-document domain("arstechnica.com") {
.article-content p {
  font-family: Georgia, serif;
  font-size: 18px;
  line-height: 28px;
  text-align: justify;
}

.article-content h2 {
  font-size: 24px;
  line-height: 36px !important;
}

aside.sidebar.story-sidebar p {
  font-family: Arial, san-serif;
  font-size: 14px;
  text-align: left;
}

aside.sidebar.story-sidebar h2 {
  line-height: 20px !important;
}
}
