/*
 * Hack A Day - Old Green Theme
 *
 * Author      : Alladdin
 * Updated     : October 13th, 2014
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  url-prefix(http://hackaday.com/),
  url-prefix(http://www.hackaday.com/)
{
  body, h1, h2, h3, h4, h5, a, label, label span {
    text-transform: none !important;
  }
  
  .comment, .comment-list {
    padding: 10px !important;
    margin: 10px 0 0 !important;
  }
  
  .entry-title, .content h1 > a {
    font-size: 32px !important;
    line-height: 32px !important;
  }
  
  body, button, input, select, textarea, h1, h1 > a, h2, h2 > a {
    color: #1a0 !important;
  }
  
  a {
    color: #4e0 !important;    
  }
  
  .widget-title, .widgettitle, .main-navigation {
    border-bottom: 3px solid #1a0;
  }
  
  .logo-img {
    width: 50px !important;
    top: 0px !important;
  }
  
  .site-title > a {
    font-size: 50px !important;
    line-height: 50px !important;
  }
  
  .site-title {
    margin-left: 70px !important;
    text-align: left !important;
  }
  
  .search-field {
    border-color: #1a0 !important;
  }
  
  .blog .entry-featured-image > a {
    background-size: 265px !important;
    height: 125px !important;
    width: 265px !important;
    margin: 25px;
  }
  
  .blog .entry-featured-image {
    position: absolute;
    float: left;
  }
  
  .blog .entry-header {
    margin-left: 285px !important; 
    min-height: 150px;
  }
}
