css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  url-prefix("http://www.catb.org/~esr/writings/taouu/taouu.html")
{
  body {
    font-size: 20px !important;
    line-height: 1.3 !important;
    max-width: 700px !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }

  a{
 text-decoration: none; 
 color: #050505;
}

/* Remove unnecessary Revision History */
  .revhistory
  {
    display:none;
  }
  
  /* Get rid of TOC */
  .toc, .list-of-figures, .list-of-tables
  {
  display:none;
  }
  
  /* Display the rest of the tables with lines. */
  table{
    border-collapse: collapse;
  }
  td{
    padding: 5px;
  }

  /* Tighten up the Name/Email slightly */
  .author h3
  {
   float:left;
  }
}
