css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("(.*)pad(.*)") {
 body,
#outerdocbody,
#innerdocbody{
 background:#555; 
}
  .sidedivdelayed{
    background:#000;
    border-right:#111 1px solid;
  }
  
  .toolbar, #editbar{
    background:#222 !important;
    border-bottom:#111 1px solid;
  }
  .popup,#chatbox,#chattext,#chaticon{
    background:#222;
    box-shadow:none;
    border-color:#222;
    color:#CCC;
  }

  #framanav.navbar,#framanav .nav-container{
    background:#000 !important;
    border-color:#000;
  }
  #pad_title{
    color:#000;
  }
  #pad_title #title{
    text-shadow:none;
  }
  .toolbar ul li a , .toolbar ul li select,.popup select{
   background:#888; 
    border-color:#333;
  }
  span[class*="author"]{
  }
  #innerdocbody a{
    color:#38D;
  }
}
