css:
@-moz-document domain("youtube.com") {
#bwp-sidebar-container {
    background: #131313;
    border: 1px solid #2A2A2A;
}

#watch-description-extras {
    border-top: 1px solid #2A2A2A;
}

.yt-card {
    border: 1px solid #2A2A2A;
}

#watch-uploader-info {
    color: #C5C5C5;
}

.show-guide #appbar-guide-menu {
    border-color: #2A2A2A;
}

ul.watch-extras-section {
    margin-top: 5px;
}

#movie_player:not(.ytp-fullscreen) .ytp-chrome-controls {
    background-color: #131313;
    color: #5A5A5A;
}
}
