css:
@-moz-document url-prefix("https://docs.scipy.org/doc/") {
  div.bodywrapper {
    font: 18px/27px "Droid Serif", Georgia, serif;
  }
  
  div.bodywrapper dt, div.bodywrapper dd {
    font: 18px/27px Georgia, serif;
  }
  
  div.bodywrapper pre {
    font: 18px/27px monospace;
  }

  div.bodywrapper li {
    line-height: 27px;
  }
}
