css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("simple-note.appspot.com"), domain("app.simplenote.com") {
    div.note {
        left: 380px !important;
    }
    div.note #txtarea {
        font-family: Meiryo !important;
        font-size: 14px !important;
        padding: 8px !important;
    }
    div.note #static_content {
        font-family: Meiryo !important;
        font-size: 14px !important;
        padding: 8px !important;
    }
    div.note #static_content pre {
        font-family: Monospace !important;
        background-color: #f8f8f8 !important;
        padding: 0px 0px 0px 4px !important;
        border: thin solid #c0c0c0;
        border-radius: 4px;
    }
    div.note #static_content p code {
        font-family: Monospace !important;
        background-color: #f8f8f8 !important;
        padding: 0px 4px 0px 4px !important;
        border: thin solid #c0c0c0;
    }
    div.note #static_content h1 {
        text-align: left !important;
        font-size: 24px !important;
        padding: 0px 0px 8px 4px !important;
        margin: 0px 0px 16px 0px !important;
        border-bottom: solid 2px #808080;
    }
    div.note #static_content h2 {
        text-align: left !important;
        font-size: 20px !important;
        padding: 0px 0px 2px 4px !important;
        margin: 0px 0px 16px 0px !important;
        border-left: solid 8px #6080c0;
        border-bottom: solid 2px #6080c0;
    }
    div.note #static_content h3 {
        text-align: left !important;
        font-size: 16px !important;
        padding: 0px 0px 0px 4px !important;
        margin: 0px 0px 16px 0px !important;
        border-left: solid 4px #6080c0;
    }
    div.note #static_content ul {
        list-style-type: disc !important;
    }
    div.note #static_content ol li {
        margin: 0px !important;
    }
}
