css:
@-moz-document domain(memrise.com) {
  .thing-show .row.primary .row-value, div.qquestion.qtext {
    font-size: 1.4em;
    font-weight: bold;
    text-shadow: 1px 1px 0 #fff;
    width: 90%;
  }

  .thing-show .row.secondary .row-value {
    font-size: 1.2em;
    font-style: italic;
    font-weight: normal;
    width: 90%;
  }

  span.shv-cloze {
    font-weight: bold;
    color: red;
  }
  
  li.shiny-box.choice.clearfix span.val {
    font-size: 0.8em;
  }
  
  div#central-area {
    width: 800px;
  }
  
  div.typing-wrapper input.roundy[type=text] {
    font-size: 1.4em;
  }
  
  ol.choices li.shiny-box {
    width: 100%;
  }
  
  ol.choices.clearfix.grid li.shiny-box.choice.clearfix {
    width: 42%;
  }
}
