#dj-button{
  border-radius: 0px !important;
  background-color: rgb(8, 18, 10) !important;
  border-radius: 10px 10px 10px 10px !important;
  box-shadow: inset 5px 10px 10px 5px rgb(31, 31, 31) !important;
}
#dj-button.is-join .left,
#dj-button.is-wait .left,
#dj-button.is-quit .left,
#dj-button.is-leave.left,
#dj-button.is-full .left,
#dj-button.is-locked .left{
  border-radius: 8px 0px 0px 0px !important;
  background-color: rgb(63, 156, 255) !important;
}
#dj-button.is-join .left,
#dj-button.is-wait .left{
  background-image: none !important;
  box-shadow: inset 0px 0px 0px 0px rgb(63, 156, 255) !important;
}
    
#woot.crowd-response,
#woot.crowd-response .bottom{
  border-radius: 8px !important;
  background-color: rgb(18, 18, 20) !important;
  border-radius: 10px 0px 0px 0px !important;
  box-shadow: inset 0px 0px 0px 0px rgb(31, 31, 31) !important;
}
#grab.crowd-response,
#grab.crowd-response .bottom{
  border-radius: 0px !important;
  background-color: rgb(18, 18, 20) !important;
  border-radius: 0px 0px 0px 0px !important;
  box-shadow: inset 0px 0px 0px 0px rgb(31, 31, 31) !important;
}
#meh.crowd-response .bottom,
#meh.crowd-response{
  border-radius: 8px !important;
  background-color: rgb(18, 18, 20) !important;
  border-radius: 0px 0px 0px 0px !important;
  box-shadow: inset 0px 0px 0px 0px rgb(31, 31, 31) !important;
}

#playlist-meta.left-meta{
  background-color: rgb(18, 18, 20)
}
#now-playing-bar{
  background-color: rgb(18, 18, 20) !important;
}
#history-button{
  background-color: rgb(18, 18, 20) !important;
}
#room-bar.bar-button{
  background-color: rgb(18, 18, 20) !important;
}
#app-menu.in-room,
#app-menu.in-room .list{
  background-color: rgb(18, 18, 20) !important;
}
#footer-user .info,
#footer-user .image{
  background-color: rgb(18, 18, 20) !important;
}
#playlist-button{
  background-color: rgb(18, 18, 20)
}

#waitlist-button.header-panel-button,
#friends-button.header-panel-button,
#users-button.header-panel-button,
#chat-button.header-panel-button{
  background-color: rgb(18, 18, 20) !important;
}
