css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('anime-planet.com') {

/* Color changes */
    /* Background */
       body {background: none repeat scroll 0% 0% #303030; 
             background-image: url('http://i.imgur.com/CKBzvOO.jpg') !important;
             background-attachment: fixed;
             background-position: center; 
             }

    /* Pastel blue buttons color changes */
       .quickFilter a {color: #5AA5C5 !important; display: inline-block !important;}
       .subNav a {color: #56004F;}

    /* Pastel green buttons color changes */
       .subnav li.selected {color: #4D760F !important;}


/* borders */
    /* SiteContainer borders */
       #siteContainer {border-width: medium 0px; margin-bottom: -345px;}
    /* Minimalistic footer mod */
       #siteFooter { background: none; border-top: 0px; }
       .pure-u-3-8, .pure-u-9-24 { width: 73.5%; }
       #siteFooter, #siteContainer:after { height: 310px; }
       #siteFooter>div{ padding:.5em }
   #siteFooter > * {margin: 70px auto; background: none !important;}
/* Anime/Manga rating position fix in Firefox browsers */
   /* Rating */
   table .myListBar .starrating{ float:none;
       width: 100px;
       position: absolute;
       right: 0; }
}
