css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://operator.mail.ru/support/staff/index.php) 
{  
.notecounterred {  
 position: fixed;
 top:0px;  
 left: 0px;
 background: #CE1707;
 background-image: none;
 opacity: 0.9;  
 font: 15px/1 "Helvetica Neue",Arial,Helvetica,sans-serif;
 text-align: center!important;
 width: 19px;
 height:15px;
 text-shadow: none;
 border: 1px solid #CE1707; 
 border-bottom-left-radius: 0px!important;
 border-top-left-radius: 0px!important;
 border-top-right-radius: 0px!important;      
 border-radius: 4px; 
 animation: none!important;
}
.ticketlockinfocontainer,.ticketbillinginfocontainer,.ticketbillinginfocontainer2 {
 font-size: 20px;
 color: #D20000 !important;
 background-image: none;
}  
}
