css:
@-moz-document url-prefix("https://delicious.com/") {
::selection {
  background: rgba(39,143,247,.5)
}

.sidebar,
.site-nav-search input {
  background: #111;
}
    
.site-nav-search input::-webkit-input-placeholder {
  color: #c1c1c1;
}

.site-nav-search input:-moz-placeholder {
  color: #c1c1c1;
}
      
.site-nav-links li.on a,
body {
  background: #444 !important;
  background-color: #444 !important;
}

.action-link {
  color: #fafafa !important;
}
  
.item:hover .action-link {
  color: #999 !important;
}
      
.item .domain {
  position: absolute;
  top: 4.7em;
  right: 1.5em;
}

.item .title-wrapper {
  padding: .5em 0 2em;
}
    

 .form-control, input[type="color"], input[type="date"], input[type="datetime-local"], input[type="datetime"], input[type="email"], input[type="month"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], input[type="time"], input[type="url"], input[type="week"], select, textarea{
    background:#414141 !important;
      color:#EEE !important;
  }
  .hero-unit .name, .hero-unit h2,.link-nav .actions > li .action-group .dropdown > a, .link-nav .actions > li > a{
    color:#CCC;
  }
  .hero-unit .info{
    text-shadow:0 0 4px #000;
  }
  .item .note{
    color:#CCC;
  }
  .item:hover .note{
    color:#FFF;
  }

}
