css:
@-moz-document url-prefix("https://www.skydesk.jp/ja/products/crm/") {
#dummy{font-weight:none;}
}
@-moz-document url-prefix("https://portal.skydesk.jp") {
.bootstrap .tileHeader, .bootstrap .ui-widget-content .ui-state-default, .bootstrap .ui-widget-header .ui-state-default{background:linear-gradient(to bottom,#f9f9f9 5%,#9df 95%,#9df 3%);color:#111;}
	#postFeedButton, #postToButton{background:linear-gradient(to bottom,#eee 5%,#eee 90%,#ddd 5%);color:#111;}
	.bootstrap #feed .tileContent, .bootstrap .feedRow{background-color:#F4FAFF;}
	body{background-color:#F6FCFF;}
	.dropdown-menu>li>a:hover, .dropdown-menu>div>li>a:hover{background-color:#9df !important}
}

@-moz-document url-prefix("https://bizcards.skydesk.jp") {
.imageDataArea{background-color:#F6FCFF}
}

@-moz-document url-prefix("https://task.skydesk.jp") {
div.zbadnotes>textarea#entertext{background-color:#F4FAFF !important;}
	tbody#listBody,tr.unselRow>td{background-color:#F4FAFF !important;}
}

@-moz-document regexp(".*skydesk.jp.*") {
::selection {background: #50AEF0;color:#E0E9F0;}
	::-moz-selection{background: #50AEF0;color:#E0E9F0;}
}
