css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Справка по цветам:

Фон #1E1E1E
Светлый фон #434346
Бордеры #434346
Шрифт #F8FEDD
Ссылка #00BFFF
П.ссылка #569CD6

Дополнительные цвета:
Берюзовый #4EC999
Оранжевый #FF7A3C
Зелёный #577430
Синий #128DBE 

*/
@-moz-document 
 url-prefix(http://wf-mr-backup.pvt), 
 url-prefix(http://wf-mr-ftp.pvt) 
 
 
{
 
 
html, tbody {
 color:#F8FEDD;
 background-color: #1E1E1E;
}
/*ссылки*/
a:link {
 color:#00BFFF;
}
a:visited {
 color:#569CD6;
} 
INPUT, SELECT , TEXTAREA, TABLE 
{
 color:#F8FEDD;
 background-color: #1E1E1E;
} 
INPUT:focus, SELECT:focus, TEXTAREA:focus 
{
 color:#F8FEDD;
 background-color: #434346;
} 
 
 
table:nth-child(n+1) > tbody:nth-child(n+1) > tr:nth-child(n+1), 
body > pre:nth-child(n+1) > table:nth-child(n+1) > tbody:nth-child(n+1) > tr:nth-child(n+1),
body > table:nth-child(n+1) > tbody:nth-child(n+1) > tr:nth-child(n+1) > th:nth-child(n+1),
body > table:nth-child(n+1) > tbody:nth-child(n+1) > tr:nth-child(n+1) > td:nth-child(n+1) {
 color:#F8FEDD;
 background-color: #1E1E1E;
 border: 1px solid #434346;
}
body > table:nth-child(n+1) > tbody:nth-child(n+1) > tr:nth-child(n+1) > td:nth-child(n+1):hover {
 color:#F8FEDD;
 background-color: #434346;
 border: 1px solid #434346;
} 
body > form:nth-child(n+1) > input:nth-child(n+1) {
 border: 1px solid #434346;
} 
body > span:nth-child(n+1), body > span:nth-child(n+1), body > span:nth-child(n+1) {
 color:#BC190B;
} 

}
