css:
@-moz-document url-prefix(http://tjournal.ru/) {

  body { background-color: rgba(31, 147, 28, 1); font-family: "Helvetica Neue", Helvetica, Arial, sans-serif; color: #000; padding: 0; margin: 0; line-height: 18px; font-size: 14px; min-width: 200px; }

  @media screen and (min-width: 1200px){
    .container { max-width: 2240px; width: auto; }
    .b-article article .wrapper-wide { width: 820px; display: block; margin: 0 auto; }
    .b-article article img { max-width: 100%; display: block; border-radius: 4px; margin: 0 auto; }
    .b-articles__b { margin: 0 -30px; padding: 20px 50px; border-top: 1px solid #edeff1; border-bottom: none; }
    .b-articles__b_relative_main:last-child { padding: 10px 50px 14px; }
  }
  
}
