css:
@-moz-document domain(d3.ru) {
.l-header-wrapper.l-header-base {
  display: none;
}

#js-header_big_login {
  display: none;
}

.b-sidebar {
  display: none;
}

.l-content_aside {
  display: none;
}

.l-content_main {
  margin: 0;
  width: 100%;
}

.l-i-content_main {
  padding: 0;
  width: 850px;
  margin: auto;
}

.b-index_posts_holder {
  padding: 0;
  font-size: 16px;
}

.post {
  margin-bottom: 35px;
  color: #5D5A56;
}

.post_body {
  margin-top: 10px;
}

.post h3 a {
  font-size: 20px;
  font-weight: bold;
  color: #2D2D2D;
}

.b-post_cut_segment.hidden {
  display: block!important;
}

.b-cut_link {
  display: none!important;
}

.vote_result {
  font-size: 18px;
  color: #BCBCBC;
}

.l-footer {
  display: none;
}

.l-i-wrapper {
  padding: 0;
}
}
