css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trello.com") {
    
    * {
        transition: all .2s ease-out !important;
    }

    body {
        background: url("http://subtlepatterns.com/patterns/binding_dark.png") repeat transparent !important;

    }

    textarea, .action-comment, .list-card, .card-detail-desc p {
   
        font-size: 16px !important;
        line-height: 1.2 !important;
    }
    .list {
        background: rgba(100, 100, 100, 0.2) !important;
        color: #FFF;
        border: 1.5px dashed rgba(255, 255, 255, 0.2);
    }
    .list-header, .card-label {
        text-align: center;
    }
    .badge-text {
        float: none !important;
    }
    .list-card-operation {
        width: 16px !important;
    }

    .button-link:hover {
        background: #444 !important;
        border-color: #111 !important;
    }
    .js-archive-card:hover {
        background: tomato !important;
        border-color: red !important;
    }
    .open-card-composer, .js-open-add-list {
        font-size: 0;
        color: #EEE;
    }
    .open-card-composer:before {
        font-size: 1rem;
    }
    .open-card-composer:hover, .js-open-add-list:hover {
        color: #222 !important;
        background: #FFF;
        text-decoration: none;
    }
    .js-open-add-list:before {
        font-size: 1rem;
    }

    .list-card {
        background-color: #333339 !important;
        color: #EEE !important;
        border-bottom: 1px solid rgba(0,0,0,0.5) !important;
        border-right: 1px solid rgba(0,0,0,0.2) !important;
        border-width: 2px !important;
        border-radius: 0px 3px;
    }

    .list-card a {
        color: #EEE !important;
    }
}
