css:
@-moz-document url("about:newtab") 
{
  body{background:#000 !important;}
  
  .newtab-title
    {
    font-size:14px !important;
    height:16px !important;
    line-height:14px !important;
    color:#DDD !important;
    background:#000 !important;
    opacity:0.7;
    }
  
  #newtab-scrollbox{background:#000 !important;}

  #newtab-search-container{display:none;}
  
  #newtab-margin-bottom{display:none;}
  
  #newtab-margin-top{display:none;}
  
  #newtab-intro-what{display:none;}

  .newtab-thumbnail{opacity:0.7;height:220px;}
  
  .newtab-cell:not([ignorehover]) > .newtab-site:hover > .newtab-link > .newtab-thumbnail {opacity: 1.0;}
  
  
  .newtab-site{
    box-shadow:0px 1px 3px #000;
  }
  
}
