css:
@-moz-document domain("gathering.tweakers.net") {

#messages, .topic_actions {
    width: auto !important;
}

div.message div.post div.messagecontent {
    width: auto !important;
    margin-right: 2em;
}

div#sidebar {
    display: none;
}

#contentArea {
    margin: 0px 2em !important;
}

#menu, #search, #contentArea, #ticker p, #about {
    width: auto !important;
}

#menubar, #layout, #bottom {
    min-width: auto !important;
}

#userbar {
    background: linear-gradient(rgb(38, 40, 40), rgb(28, 30, 30)) repeat scroll 0px 0px rgb(33, 35, 35);
    z-index: 10;
}

div.userdetails .userdata {
    padding: 0px !important;
}

.action_list {
    margin: 0px !important;
}

div.title {
    display: none;
}

}
