css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com")

{

.tab_post_type .post_type_icon, .tab-post-type .post-type-icon
{
    background-image: url("/*[[theimage]]*/") !important;
}

.tab-post-types .tab-post-type:nth-child(1) .post-type-icon
{
    background-position: 0px 12px !important;
}

.tab-post-types .tab-post-type:nth-child(2) .post-type-icon
{
    background-position: -115px 12px !important;
}

.tab-post-types .tab-post-type:nth-child(3) .post-type-icon
{
    background-position: -230px 12px !important;
}

.tab-post-types .tab-post-type:nth-child(4) .post-type-icon
{
    background-position: -345px 12px !important;
}

.tab-post-types .tab-post-type:nth-child(5) .post-type-icon
{
    background-position: -465px 12px !important;
}

.tab-post-types .tab-post-type:nth-child(6) .post-type-icon
{
    background-position: -585px 12px !important;
}

.tab-post-types .tab-post-type:nth-child(7) .post-type-icon
{
    background-position: -707px 12px !important;
}
    
.post-tab-switching.finish-animating .tab-post-type:hover .post-type-icon
{
margin-top: -5px !important;
transform: none !important;
}

.tab-post-type .post-type-name .post-type-name-span
{
font-size: 14px !important;
letter-spacing: 1px !important;
}
}
