css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	
	body  { background-color: #ffffff; ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #ffffff; ;}
	pre  { color: #bbbbbb ;}

	.hs-chr  { color: #606060; font-weight: bold; background-color: #fff0f0 ;}
.hs-comment  { color: #808080 ;}
.hs-comment a  { color: #808080 ;}
.hs-conid  { color: #303090; font-weight: bold ;}
.hs-conop  { color: #303030 ;}
.hs-cpp  { color: #008000; font-weight: bold ;}
.hs-definition  { color: #0060B0; font-weight: bold ;}
.hs-keyglyph  { color: #000000; font-weight: bold ;}
.hs-keyword  { color: #008000; font-weight: bold ;}
.hs-layout { color: #000000;}
.hs-num  { color: #6000E0; font-weight: bold ;}
.hs-str  { background-color: #fff0f0 ;}
.hs-varid { color: #000000;}
.hs-varop  { color: #303030 ;}
background  { background-color: #ffffff; ;}
foreground  { color: #bbbbbb ;}


}
