css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("golang.org") {
  * {font-family: "Lato"; font-weight: 400;}
  #nav * {font-size: 1.01em}
  body {font-size: 1.01em; line-height: 1.45; color:rgba(90, 78, 78, 0.79)}
  h1, h2, h3, h4, .title {font-weight: 300; font-size: 1.6em;}
  p, ul, ol {word-spacing: 1.4px;}
  .CodeMirror {font-family: "Source Code Pro"; line-height: 1.15;}
  textarea, pre, pre > *, code, code > * {
   font-family: "Source Code Pro" !important; 
   font-weight: normal;
   overflow: auto;
  }
}

@-moz-document domain("play.golang.org") {
  * {font-family: "Source Code Pro" !important;}
  #code, #output, pre, .lines {line-height: 1.5;}
  #importsBox {padding-top: 6px;}
  #importsBox, input {font-size: 15px !important;}
}
