css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	body  { background-color: #ffffff; ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #ffffff; ;}
	pre  { color: #bbbbbb ;}

	.hs-chr  { color: #0000FF ;}
.hs-comment  { color: #008800; font-style: italic ;}
.hs-comment a  { color: #008800; font-style: italic ;}
.hs-conid  { color: #000080; font-weight: bold ;}
.hs-conop { color: #000000;}
.hs-cpp  { color: #000080; font-weight: bold ;}
.hs-definition { color: #000000;}
.hs-keyglyph  { font-weight: bold ;}
.hs-keyword  { color: #000080; font-weight: bold ;}
.hs-layout { color: #000000;}
.hs-num  { color: #0000FF ;}
.hs-str  { color: #0000FF ;}
.hs-varid { color: #000000;}
.hs-varop { color: #000000;}
background  { background-color: #ffffff; ;}
foreground  { color: #bbbbbb ;}


}
