css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	body  { background-color: #f8f8f8; ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #f8f8f8; ;}
	pre  { color: #bbbbbb ;}

	.hs-chr  { color: #BB6622; font-weight: bold ;}
.hs-comment  { color: #008800; font-style: italic ;}
.hs-comment a  { color: #008800; font-style: italic ;}
.hs-conid  { color: #00BB00; font-weight: bold ;}
.hs-conop  { color: #666666 ;}
.hs-cpp  { color: #AA22FF; font-weight: bold ;}
.hs-definition  { color: #00A000 ;}
.hs-keyglyph  { color: #AA22FF; font-weight: bold ;}
.hs-keyword  { color: #AA22FF; font-weight: bold ;}
.hs-layout { color: #000000;}
.hs-num  { color: #666666 ;}
.hs-str  { color: #BB4444 ;}
.hs-varid { color: #000000;}
.hs-varop  { color: #666666 ;}
background  { background-color: #f8f8f8; ;}
foreground  { color: #bbbbbb ;}

}
