css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	body  { background-color: #111111; color: #ffffff ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #111111; color: #ffffff ;}
	pre  { color: #888888 ;}

	.hs-chr  { color: #0086d2 ;}
.hs-comment  { color: #008800; font-style: italic; background-color: #0f140f ;}
.hs-comment a  { color: #008800; font-style: italic; background-color: #0f140f ;}
.hs-conid  { color: #cdcaa9; font-weight: bold ;}
.hs-conop  { color: #ffffff ;}
.hs-cpp  { color: #fb660a; font-weight: bold ;}
.hs-definition  { color: #ff0086; font-weight: bold ;}
.hs-keyglyph  { color: #ffffff ;}
.hs-keyword  { color: #fb660a; font-weight: bold ;}
.hs-layout  { color: #ffffff ;}
.hs-num  { color: #0086f7; font-weight: bold ;}
.hs-str  { color: #0086d2 ;}
.hs-varid  { color: #ffffff ;}
.hs-varop  { color: #ffffff ;}
background  { background-color: #111111; color: #ffffff ;}
foreground  { color: #888888 ;}

}
