css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	body  { background-color: #f0f3f3; ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #f0f3f3; ;}
	pre  { color: #bbbbbb ;}

	.hs-chr  { color: #CC3300; font-weight: bold ;}
.hs-comment  { color: #0099FF; font-style: italic ;}
.hs-comment a  { color: #0099FF; font-style: italic ;}
.hs-conid  { color: #007788; font-weight: bold ;}
.hs-conop  { color: #555555 ;}
.hs-cpp  { color: #006699; font-weight: bold ;}
.hs-definition  { color: #CC00FF ;}
.hs-keyglyph  { color: #000000; font-weight: bold ;}
.hs-keyword  { color: #006699; font-weight: bold ;}
.hs-layout { color: #000000;}
.hs-num  { color: #FF6600 ;}
.hs-str  { color: #CC3300 ;}
.hs-varid { color: #000000;}
.hs-varop  { color: #555555 ;}
background  { background-color: #f0f3f3; ;}
foreground  { color: #bbbbbb ;}

}
