css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	body  { background-color: #ffffff; ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #ffffff; ;}
	pre  { color: #bbbbbb ;}

	.hs-chr  { color: #0044dd; background-color: #fff0f0 ;}
.hs-comment  { color: #888888 ;}
.hs-comment a  { color: #888888 ;}
.hs-conid  { color: #888888; font-weight: bold ;}
.hs-conop { color: #000000;}
.hs-cpp  { color: #008800; font-weight: bold ;}
.hs-definition  { color: #0066bb; font-weight: bold ;}
.hs-keyglyph  { color: #008800 ;}
.hs-keyword  { color: #008800; font-weight: bold ;}
.hs-layout { color: #000000;}
.hs-num  { color: #0000DD; font-weight: bold ;}
.hs-str  { color: #dd2200; background-color: #fff0f0 ;}
.hs-varid { color: #000000;}
.hs-varop { color: #000000;}
background  { background-color: #ffffff; ;}
foreground  { color: #bbbbbb ;}

}
