css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	body  { background-color: #eeeedd; ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #eeeedd; ;}
	pre  { color: #bbbbbb ;}

	.hs-chr  { color: #CD5555 ;}
.hs-comment  { color: #228B22 ;}
.hs-comment a  { color: #228B22 ;}
.hs-conid  { color: #a7a7a7; font-weight: bold ;}
.hs-conop { color: #000000;}
.hs-cpp  { color: #8B008B; font-weight: bold ;}
.hs-definition  { color: #008b45 ;}
.hs-keyglyph  { color: #8B008B ;}
.hs-keyword  { color: #8B008B; font-weight: bold ;}
.hs-layout { color: #000000;}
.hs-num  { color: #B452CD ;}
.hs-str  { color: #CD5555 ;}
.hs-varid { color: #000000;}
.hs-varop { color: #000000;}
background  { background-color: #eeeedd; ;}
foreground  { color: #bbbbbb ;}

}
