css:
@-moz-document domain("caliser.com") {

body:not(.home) .td-menu-placeholder {height: 0;}
/*.td-menu-placeholder,*/
body:not(.home) .td-menu-background/*,
body:not(.home) .td-header-bg.customize-mobile-layout-1*/ {
  position: fixed; top: 0; left: 0;
  clip: rect(0, 8px, 10mm, 0);
  border-left: 8px solid blue;
  z-index: 2;
}
body:not(.home) .td-menu-background:hover/*,*/
/*.td-menu-placeholder:hover,*/
/*.td-header-bg.customize-mobile-layout-1:hover*/ {
  clip: rect(0, 1920px, 1080px, 0);
  border-left-width: 0;
  z-index: 1;
}
/*body:not(.home) .td-header-bg.customize-mobile-layout-1 {
  clip: rect(10mm, 8px, 20mm, 0);
  border-left-color: red;
}*/

body:not(.home) .td-header-menu-wrap,
body:not(.home) .td-header-bg.customize-mobile-layout-1 {
  display: none;
}

/*.container.td-page-wrap > .row:first-child,*/
.social-sharing-mobile-layout-1 {
  display: none;
}
IMG.alignnone.size-full {
  width: auto !important;
  height: auto !important;
  
}
  /* revcontent ads */
[onclick*="adClicked"],
[class*="-ads-"], /*.advads_widget,*/


  /*ads within slideshow photos*/
.kskdDiv {
  display: none !important;
}

}
