css:
@-moz-document domain("twitter.com") {
html {
color:rgb(192, 0, 192);font-family:Comic Sans MS,sans-serif;
-ms-text-size-adjust:100%;
-webkit-text-size-adjust:100%;
}

body {
color:rgb(192, 0, 192);font-family:Comic Sans MS,"Helvetica Neue",Helvetica,Arial,sans-serif;
background:#fff;
color:#292f33;
font-size:14px;
line-height:18px;
overflow-y:scroll;
margin:0;
}


code,kbd,pre,samp {
color:rgb(192, 0, 192);font-family:Comic Sans MS,monospace,monospace;
font-size:1em;
}

body.ms-windows {
color:rgb(192, 0, 192);font-family:Comic Sans MS,Arial,sans-serif;
}

body.ar,body.ur,body.fa {
color:rgb(192, 0, 192);font-family:Comic Sans MS,Tahoma,Arial,sans-serif;
}


body.ja {
color:rgb(192, 0, 192);font-family:Comic Sans MS,Arial,"ヒラギノ角ゴ Pro W3","Hiragino Kaku Gothic Pro",Osaka,メイリオ,Meiryo,"ＭＳ Ｐゴシック","MS PGothic",sans-serif;
}


.tweet .js-tweet-text {
white-space:pre-wrap;
font-family:Comic Sans MS;
color:rgb(192, 0, 192);
}

.media-not-displayed p {
font-size:12px;
color:rgb(192, 0, 192);
  font-family:HelveticaNeue,"Helvetica Neue",Helvetica,Arial,sans-serif;
line-height:15px;
color:#66757f;
padding:5px 0;
}


body.ar .media-not-displayed p,body.fa .media-not-displayed p,body.ur .media-not-displayed p {
color:rgb(192, 0, 192);font-family:Tahoma,HelveticaNeue,"Helvetica Neue",Helvetica,Arial,sans-serif;
}

.top-timeline-tweetbox .timeline-tweet-box .tweet-form .tweet-box {
font-size:14px;
padding:8px 10px;
color:rgb(192, 0, 192);
}

.ProfileTweet-text {
color: rgb(192, 0, 192);
font-weight: 400;
font-size: 16px;
line-height: 22px;
margin-bottom: 5px;
white-space: pre-wrap;
}

@font-face{
  color:rgb(192, 0, 192);
}
.Icon:before
{
  color:rgb(192, 0, 192);
}
}
