css:
@-moz-document domain("wiki.hybris.com") {
article {
  width: 80%;
  margin-left: auto;
  margin-right: auto;
}
body, .aui-theme-default {
  background: #2B2B2B !important;
  color:#C0C0C0 !important;
  font-size: 18pt !important;
}
h3 {
  font-size: 20pt !important;
  line-height: 24pt !important;
}
.pdl .syntaxhighlighter table td.code code, .pdl .syntaxhighlighter table td.code div {
  font-size: 15pt  !important;
  line-height: 18pt !important;
}
body, #rw_main_column .rw_content {
  background: #2B2B2B !important;
}
.code, .preformatted, div.panelHeader.pdl, .syntaxhighlighter, .pdl .panelContent, .syntaxhighlighter .line.alt2, .syntaxhighlighter .line.alt1,
.aui-message.warning, .aui-message.warning.shadowed.information-macro, .aui-message.success, .aui-message.hint, .aui-message.problem {
  background: #3A3A3A !important;
}
.wiki-content table.tablesorter > thead > tr > th {
	background: #4A4A4A center right no-repeat;
}
.code, .preformatted, h1, h2, h3, h4, .wiki-content h1, .wiki-content h2, .wiki-content h3, .wiki-content h4, .pagetitle, .steptitle, .substeptitle, .formtitle, table.confluenceTable td.confluenceTd.highlight, table.confluenceTable td.confluenceTd.highlight > p, table.confluenceTable th.confluenceTh, table.confluenceTable th.confluenceTh > p, .wiki-content .tablesorter-header-inner, table.admin th, .form-element-large, .form-element-small, #toolbar #format-dropdown .format-h1 a, #toolbar #format-dropdown .format-h2 a, #toolbar #format-dropdown .format-h3 a, #toolbar #format-dropdown .format-h4 a, .panelHeader.pdl>b, .syntxhighlighter .plain, .aui-message.warning, .aui-message.warning.shadowed.information-macro,
.aui-message.success, .syntaxhighlighter .plain, .syntaxhighlighter .plain a, .aui-message.hint, .aui-message.problem{
  color: #CCCCCC !important;
}
#rw_main_column .rw_content, .panel, div.panel.pdl {
  border-color: #2A2A2A !important;
}
#rw_wrapper #content.page, #rw_wrapper #content.blogpost  {
  background-color: #2A2A2A !important;
}
div.panelHeader.pdl {
  color: #CCC;
  border-bottom-color: #2A2A2A;
}
    
.syntaxhighlighter .string, .syntaxhighlighter .string a, .wiki-content a, .wiki-content a:link, .wiki-content a:visited, .wiki-content a:focus, .wiki-content a:hover, .wiki-content a:active{
    color: #0071A6 !important;
}
}
